import React, { useState } from 'react';
import { ThemeProvider, CssBaseline, Container, Box, Paper, Typography, Stepper, Step, StepLabel } from '@mui/material';
import { createTheme } from '@mui/material/styles';

// Create components directory and import components
import InvestmentProfile from './components/InvestmentProfile';
import PortfolioRecommendation from './components/PortfolioRecommendation';
import ReturnCalculator from './components/ReturnCalculator';
import CustomCalculator from './components/CustomCalculator';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const steps = [
  'Determine Investment Profile',
  'Portfolio Recommendation',
  'Return Calculation',
  'Custom Calculator',
];

function App() {
  const [activeStep, setActiveStep] = useState(0);
  const [investorProfile, setInvestorProfile] = useState<'conservative' | 'moderate' | 'aggressive' | null>(null);
  const [investmentAmount, setInvestmentAmount] = useState<number>(0);

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const getStepContent = (step: number) => {
    switch (step) {
      case 0:
        return <InvestmentProfile onNext={handleNext} onProfileSelect={setInvestorProfile} />;
      case 1:
        return (
          <PortfolioRecommendation
            profile={investorProfile}
            onNext={handleNext}
            onBack={handleBack}
            onAmountChange={setInvestmentAmount}
          />
        );
      case 2:
        return (
          <ReturnCalculator
            profile={investorProfile}
            amount={investmentAmount}
            onNext={handleNext}
            onBack={handleBack}
          />
        );
      case 3:
        return <CustomCalculator onBack={handleBack} />;
      default:
        return 'Unknown step';
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container>
        <Box sx={{ mt: 4, mb: 4 }}>
          <Paper elevation={3} sx={{ p: 4 }}>
            <Typography variant="h4" component="h1" gutterBottom align="center">
              GuiaInvest - Investment Guide
            </Typography>
            <Stepper activeStep={activeStep} sx={{ pt: 3, pb: 5 }}>
              {steps.map((label) => (
                <Step key={label}>
                  <StepLabel>{label}</StepLabel>
                </Step>
              ))}
            </Stepper>
            {getStepContent(activeStep)}
          </Paper>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
